methods.source:
start () {
    if (this.#started) {
        return false
    }

    this.#started = true
    this.onStart?.()
    this.emit('start')

    return true
}

stop () {
    if (!this.#started) {
        return false
    }

    this.#started = false
    this.onStop?.()
    this.emit('stop')

    return true
}

dispose () {
    if (this.#disposed) {
        return false
    }

    this.#disposed = true
    this.stop()

    this.cleanExternalListeners()

    this.#childrenRegistry.forEach(child => {
        if (child && !child.disposed) {
            child.dispose()
        }
    })
    this.#childrenRegistry.clear()

    this.onDispose?.()
    this.emit('dispose')
    this.removeListeners()

    return true
}

install (host, options) {
    if (this.#installed) {
        return this.uninstall()
    }

    this.#host = host
    if (this.$bind) {
        host[this.$bind] = this
    }
    this.#installed = true

    this.onInstall?.(host, options)

    return true
}

uninstall () {
    if (!this.#installed) {
        return false
    }

    this.cleanDelegations()
    this.cleanEventDelegations()
    this.onUninstall?.(this.#host)
    this.#installed = false
    this.#host = null

    return true
}

create (Child, options = {}) {
    options.$category ||= Child.$category
    options.$name ||= Child.$name || options.$category
    options.$id ||= uniqueId(this.#childrenRegistry, options.$name)
    options.$lifecycle = options.$lifecycle ?? Child.$lifecycle ?? true
    options.$eagerStart = options.$eagerStart ?? Child.$eagerStart ?? true

    return this.#addChild(new Child(options), options)
}

getChild (name) {
    return this.#childrenRegistry.get(name) || null
}

hasChild (name) {
    return this.#childrenRegistry.has(name)
}

removeChild (name) {
    const child = this.#childrenRegistry.get(name)

    if (!child) {
        return false
    }

    unregisterChild(this, child)

    return true
}

listNamesFor (key, indexName = '$category') {
    return this.#childrenRegistry.lookupKeys(indexName, key)
}

lookup (indexName, key) {
    return this.#childrenRegistry.lookup(indexName, key)
}

childrenByCategory (category) {
    return this.lookup('$category', category)
}

hasTag (tag) {
    return this.#tags.has(tag)
}

addTag (tag) {
    if (this.#tags.has(tag)) {
        return false
    }
    this.#tags.add(tag)
    return true
}

removeTag (tag) {
    return this.#tags.delete(tag)
}

hasTags (tags) {
    if (typeof tags === 'string') {
        return this.hasTag(tags)
    }

    return Array.isArray(tags) && tags.every(tag => this.#tags.has(tag))
}

childrenByTags (tags) {
    const tagArray = Array.isArray(tags) ? tags : [tags]

    if (tagArray.length === 0) {
        return []
    }

    const indexKey = getTagIndexKey(tagArray)

    if (this.#tagIndexes.has(indexKey)) {
        return this.#childrenRegistry.lookup(indexKey, indexKey)
    }

    return this.#childrenRegistry.all.filter(
        child => tagArray.every(tag => child.$tags?.includes(tag))
    )
}

addTagsIndex (tags) {
    if (!Array.isArray(tags) || tags.length === 0) {
        return false
    }

    const indexKey = getTagIndexKey(tags)

    if (this.#tagIndexes.has(indexKey)) {
        return false
    }

    this.#childrenRegistry.addIndex(indexKey, child => {
        const hasAllTags = tags.every(tag => child.tags?.has(tag))
        return hasAllTags ? indexKey : null
    })

    this.#tagIndexes.set(indexKey, tags)

    this.#childrenRegistry.forEach(child => {
        if (child.tags) {
            this.#setupTagIndexListeners(child)
        }
    })

    return true
}

removeTagsIndex (tags) {
    const indexKey = getTagIndexKey(tags)

    if (!this.#tagIndexes.has(indexKey)) {
        return false
    }

    this.#childrenRegistry.removeIndex(indexKey)
    this.#tagIndexes.delete(indexKey)
    return true
}

delegateTo (host, names) {
    delegateProperties(host, this, names)

    const propertyNames = Array.isArray(names)
        ? names
        : Object.values(names)

    this.#delegations.push({host, propertyNames})
}

cleanDelegations () {
    for (const {host, propertyNames} of this.#delegations) {
        for (const name of propertyNames) {
            delete host[name]
        }
    }
    this.#delegations.length = 0
}

delegateEventsTo (host, events, namespace) {
    const eventArray = Array.isArray(events) ? events : Object.keys(events)
    const callbacks = []

    for (const event of eventArray) {
        const prefixedEvent = namespace ? `${namespace}:${event}` : event
        const callback = (...args) => host.emit(prefixedEvent, ...args)
        this.on(event, callback)
        callbacks.push({event, callback})
    }

    this.#eventDelegations.push({callbacks})
}

cleanEventDelegations () {
    for (const {callbacks} of this.#eventDelegations) {
        for (const {event, callback} of callbacks) {
            this.off(event, callback)
        }
    }
    this.#eventDelegations.length = 0
}

query (selector) {
    return perkyQuery(this, selector)
}

queryAll (selector) {
    return perkyQueryAll(this, selector)
}

getters.source:
get $status () {
    if (!this.#lifecycle) {
        return 'static'
    }

    if (this.#disposed) {
        return 'disposed'
    }

    if (this.#started) {
        return 'started'
    }

    return 'stopped'
}

setters.source:
set $id (newId) {
    const oldId = this.#id

    if (oldId !== newId) {
        this.#id = newId
        this.emit('$id:changed', newId, oldId)
    }
}

set $name (newName) {
    const oldName = this.#name

    if (oldName !== newName) {
        this.#name = newName
        this.emit('$name:changed', newName, oldName)
    }
}

set $category (newCategory) {
    const oldCategory = this.#category

    if (oldCategory !== newCategory) {
        this.#category = newCategory
        this.emit('$category:changed', newCategory, oldCategory)
    }
}

set $bind (newBind) {
    const oldBind = this.#bind

    if (oldBind !== newBind) {
        this.#bind = newBind
        this.emit('$bind:changed', newBind, oldBind)
    }
}

set $tags (newTags) {
    this.#tags.clear()
    if (Array.isArray(newTags)) {
        newTags.forEach(tag => this.#tags.add(tag))
    }
}

functions.source:
function toCamelCase (string) {
    return inflector.toCamelCase(string)
}

function toPascalCase (string) {
    return inflector.toPascalCase(string)
}

function toSnakeCase (string) {
    return inflector.toSnakeCase(string)
}

function toKebabCase (string) {
    return inflector.toKebabCase(string)
}

function toHumanCase (string) {
    return inflector.toHumanCase(string)
}

function uniqueId (collection, prefix) {
    if (!prefix) {
        prefix = collection
        collection = 'default'
    }

    if (!idCounter[collection]) {
        idCounter[collection] = {}
    }

    if (!idCounter[collection][prefix]) {
        idCounter[collection][prefix] = 0
    }

    const current = idCounter[collection][prefix]
    idCounter[collection][prefix]++

    return current === 0 ? prefix : `${prefix}_${current}`
}

function resetUniqueId (collection, prefix) {
    if (prefix) {
        if (idCounter[collection]) {
            idCounter[collection][prefix] = 0
        }
    } else {
        idCounter[collection] = {}
    }
}

function singularize (word) {
    return inflector.singular(word)
}

function pluralize (word, count, inclusive) {
    return inflector.pluralize(word, count, inclusive)
}

function plural (word) {
    return inflector.plural(word)
}

function isPlural (word) {
    return inflector.isPlural(word)
}

function isSingular (word) {
    return inflector.isSingular(word)
}

function setDefaults (data, defaults) {
    return deepMerge(defaults, data)
}

function getNestedValue (obj, path) {
    if (!path) {
        return obj
    }

    const parts = path.split('.')
    let current = obj

    for (const part of parts) {
        if (current === undefined || current === null) {
            return undefined
        }
        current = current[part]
    }

    return current
}

function setNestedValue (obj, path, value) {
    const parts = path.split('.')
    let current = obj

    for (let i = 0; i < parts.length - 1; i++) {
        const part = parts[i]
        if (!current[part]) {
            current[part] = {}
        }
        current = current[part]
    }

    current[parts[parts.length - 1]] = value
    return obj
}

function deepMerge (target, source) {
    return deepMergeInternal(target, source, new WeakSet())
}

function deepMergeInternal (target, source, seen) {
    if (!source) {
        return target
    }

    if (typeof source === 'object' && source !== null) {
        if (seen.has(source)) {
            return source
        }
        seen.add(source)
    }

    const sourceIsArray = Array.isArray(source)
    const targetIsArray = Array.isArray(target)

    if (sourceIsArray !== targetIsArray) {
        return cloneIfNeeded(source, seen)
    }

    if (sourceIsArray) {
        return mergeArrays(target, source, seen)
    }

    return mergeObject(target, source, seen)
}

function exportValue (value) {

    if (value && typeof value === 'object') {
        if (typeof value.export === 'function') {
            return value.export()
        }

        if (Array.isArray(value)) {
            return value.map(item => exportValue(item))
        }

        const result = {}
        for (const key in value) {
            if (Object.hasOwn(value, key)) {
                result[key] = exportValue(value[key])
            }
        }
        return result
    }

    return value
}

function isNonNullObject (value) {
    return Boolean(value) && typeof value === 'object'
}

function isSpecial (value) {
    const stringValue = Object.prototype.toString.call(value)
    return stringValue === '[object RegExp]' || stringValue === '[object Date]'
}

function isMergeableObject (value) {
    return isNonNullObject(value) && !isSpecial(value)
}

function emptyTarget (value) {
    return Array.isArray(value) ? [] : {}
}

function cloneIfNeeded (value, seen) {
    return isMergeableObject(value) ? deepMergeInternal(emptyTarget(value), value, seen) : value
}

function mergeArrays (target, source, seen) {
    return target.concat(source).map(element => cloneIfNeeded(element, seen))
}

function getKeys (target) {
    const symbols = Object.getOwnPropertySymbols
        ? Object.getOwnPropertySymbols(target).filter(symbol =>
            Object.propertyIsEnumerable.call(target, symbol))
        : []
    return Object.keys(target).concat(symbols)
}

function mergeObject (target, source, seen) {
    const destination = {}

    if (isMergeableObject(target)) {
        getKeys(target).forEach(key => {
            destination[key] = cloneIfNeeded(target[key], seen)
        })
    }

    getKeys(source).forEach(key => {
        if ((key in target) && isMergeableObject(source[key])) {
            destination[key] = deepMergeInternal(target[key], source[key], seen)
        } else {
            destination[key] = cloneIfNeeded(source[key], seen)
        }
    })

    return destination
}

function formatNumber (n) {
    if (typeof n !== 'number') {
        return String(n)
    }
    return Number.isInteger(n) ? String(n) : n.toFixed(2)
}

function formatBytes (bytes) {
    if (bytes === 0) {
        return '0 B'
    }

    const units = ['B', 'KB', 'MB', 'GB']
    const i = Math.floor(Math.log(bytes) / Math.log(1024))
    const value = bytes / Math.pow(1024, i)

    return `${value.toFixed(i > 1 ? 2 : 0)} ${units[i]}`
}

function delegateProperties (receiver, source, names) {
    if (Array.isArray(names)) {
        names.forEach(name => delegateProperty(receiver, source, name, name))
    } else if (typeof names === 'object') {
        Object.entries(names).forEach(([sourceName, receiverName]) => {
            delegateProperty(receiver, source, sourceName, receiverName)
        })
    }
}

function delegateProperty (receiver, source, sourceName, receiverName) {
    const descriptor = Object.getOwnPropertyDescriptor(source, sourceName)

    if (descriptor && (descriptor.get || descriptor.set)) {
        Object.defineProperty(receiver, receiverName, {
            get: descriptor.get ? descriptor.get.bind(source) : undefined,
            set: descriptor.set ? descriptor.set.bind(source) : undefined,
            enumerable: true,
            configurable: true
        })
    } else if (typeof source[sourceName] === 'function') {
        receiver[receiverName] = source[sourceName].bind(source)
    } else {
        Object.defineProperty(receiver, receiverName, {
            get: () => source[sourceName],
            set: (value) => {
                source[sourceName] = value
            },
            enumerable: true,
            configurable: true
        })
    }
}

statics.source:
static normalizeBindings (controllerName) {
    const bindings = this.bindings
    const normalized = []

    for (const [actionName, bindingDef] of Object.entries(bindings)) {
        const bindingConfigs = normalizeBindingDefinition(bindingDef)

        for (const config of bindingConfigs) {
            normalized.push({
                action: actionName,
                key: config.key,
                scoped: config.scoped ?? false,
                eventType: config.eventType ?? 'pressed',
                controllerName: config.scoped ? controllerName : null
            })
        }
    }

    return normalized
}

methods.source:
addAction (actionName, action) {
    if (!this.#actionList.includes(actionName)) {
        this.#actionList.push(actionName)
    }
    return this.#actions.set(actionName, action)
}

getAction (actionName) {
    return this.#actions.get(actionName)
}

removeAction (actionName) {
    return this.#actions.delete(actionName)
}

hasAction (actionName) {
    return this.getAction(actionName) !== undefined || typeof this[actionName] === 'function'
}

shouldPropagate (actionName) {
    const ControllerClass = this.constructor
    return ControllerClass.propagable && Array.isArray(ControllerClass.propagable)
        ? ControllerClass.propagable.includes(actionName)
        : false
}

listActions () {
    return [...this.#actionList]
}

listActionsWithParams () {
    const result = []

    for (const actionName of this.#actionList) {
        const action = this.getAction(actionName) || this[actionName]
        const params = typeof action === 'function' ? extractFunctionParams(action) : []
        result.push({name: actionName, params})
    }

    return result
}

execute (actionName, ...args) {
    const action = this.getAction(actionName) || this[actionName]

    if (typeof action === 'function') {
        action.call(this, ...args)
    }

    this.emit(actionName, ...args)
    this.emit('action', actionName, ...args)
}

constructor.source:
constructor (options = {}) {
    super(options)
    this.context = {}
    this.#actionList = extractPrototypeMethods(this)
}

methods.source:
addAction (actionName, action) {
    if (!this.mainController) {
        logger.warn('No main controller available. Cannot add action.')
        return false
    }
    return this.mainController.addAction(actionName, action)
}

removeAction (actionName) {
    if (!this.mainController) {
        logger.warn('No main controller available. Cannot remove action.')
        return false
    }
    return this.mainController.removeAction(actionName)
}

register (name, Controller = ActionController) {
    return this.create(Controller, {
        $id: name,
        $category: 'controller'
    })
}

unregister (name) {
    const controller = this.getChild(name)

    if (!controller) {
        return false
    }

    const stackIndex = this.#activeControllers.indexOf(name)
    if (stackIndex !== -1) {
        this.#activeControllers.splice(stackIndex, 1)
    }

    return this.removeChild(name)
}

getController (name) {
    return this.getChild(name)
}

setActive (names) {
    const nameArray = Array.isArray(names) ? names : [names]

    for (const name of nameArray) {
        if (!this.hasChild(name)) {
            logger.warn(`Controller "${name}" not found. Cannot set as active controller.`)
            return false
        }
    }

    this.#activeControllers = [...nameArray]
    this.emit('controllers:activated', nameArray)

    return true
}

getActive () {
    return [...this.#activeControllers]
}

pushActive (name) {
    if (!this.hasChild(name)) {
        logger.warn(`Controller "${name}" not found`)
        return false
    }

    if (this.#activeControllers.includes(name)) {
        return false
    }

    this.#activeControllers.push(name)
    this.emit('controller:pushed', name, this.#activeControllers.length)

    return true
}

popActive () {
    if (this.#activeControllers.length === 0) {
        logger.warn('Active controllers stack is empty')
        return null
    }

    const popped = this.#activeControllers.pop()
    this.emit('controller:popped', popped, this.#activeControllers.length)

    return popped
}

clearActive () {
    this.#activeControllers = []
    this.emit('controllers:cleared')
}

execute (actionName, ...args) {
    if (this.#activeControllers.length === 0) {
        logger.warn('No active controllers')
        return
    }

    const registry = this.childrenRegistry

    for (let i = this.#activeControllers.length - 1; i >= 0; i--) {
        const controllerName = this.#activeControllers[i]
        const controller = registry.get(controllerName)

        if (!controller) {
            logger.warn(`Controller "${controllerName}" not found in registry but present in active stack`)
            continue
        }

        const hasAction = controller.hasAction(actionName)

        controller.execute(actionName, ...args)

        if (hasAction && !controller.shouldPropagate(actionName)) {
            return
        }
    }
}

executeTo (name, actionName, ...args) {
    const controller = this.getController(name)

    if (controller && this.#isControllerActive(name)) {
        if (typeof controller.execute === 'function') {
            controller.execute(actionName, ...args)
        } else if (typeof controller[actionName] === 'function') {
            controller[actionName](...args)
        }
    }
}

dispatchAction (binding, ...args) {
    if (binding.controllerName) {
        this.executeTo(binding.controllerName, binding.actionName, ...args)
    } else {
        this.execute(binding.actionName, ...args)
    }
}

listControllers () {
    return this.listNamesFor('controller')
}

listAllActions () {
    const allActions = new Map()
    const registry = this.childrenRegistry

    for (const [name, controller] of registry.entries) {
        if (typeof controller.listActionsWithParams === 'function') {
            allActions.set(name, controller.listActionsWithParams())
        } else if (typeof controller.listActions === 'function') {
            allActions.set(name, controller.listActions().map(n => ({name: n, params: []})))
        }
    }

    return allActions
}

plural (word) {
    return this.#replaceWord(
        this.#irregularSingles,
        this.#irregularPlurals,
        this.#pluralRules
    )(word)
}

singular (word) {
    return this.#replaceWord(
        this.#irregularPlurals,
        this.#irregularSingles,
        this.#singularRules
    )(word)
}

isPlural (word) {
    return this.#checkWord(
        this.#irregularSingles,
        this.#irregularPlurals,
        this.#pluralRules
    )(word)
}

isSingular (word) {
    return this.#checkWord(
        this.#irregularPlurals,
        this.#irregularSingles,
        this.#singularRules
    )(word)
}

pluralize (word, count, inclusive) {
    const pluralized = count === 1 ? this.singular(word) : this.plural(word)
    return (inclusive ? count + ' ' : '') + pluralized
}

toPascalCase (string) {
    return this.toCamelCase(string).replace(/^[a-z]/, letter => letter.toUpperCase())
}

addPluralRule (rule, replacement) {
    this.#pluralRules.push([sanitizeRule(rule), replacement])
}

addSingularRule (rule, replacement) {
    this.#singularRules.push([sanitizeRule(rule), replacement])
}

addUncountableRule (word) {
    if (typeof word === 'string') {
        this.#uncountables[word.toLowerCase()] = true
        return
    }

    this.addPluralRule(word, '$0')
    this.addSingularRule(word, '$0')
}

addIrregularRule (single, pluralForm) {
    const s = single.toLowerCase()
    const p = pluralForm.toLowerCase()

    this.#irregularSingles[s] = p
    this.#irregularPlurals[p] = s
}

constructor.source:
constructor () {
    this.#loadIrregulars()
    this.#loadPlurals()
    this.#loadSingulars()
    this.#loadUncountables()
}

methods.source:
log (...items) {
    this.#emit('notice', ...items)
}

info (...items) {
    this.#emit('info', ...items)
}

notice (...items) {
    this.#emit('notice', ...items)
}

warn (...items) {
    this.#emit('warn', ...items)
}

error (...items) {
    this.#emit('error', ...items)
}

success (...items) {
    this.#emit('success', ...items)
}

clear () {
    const entry = {event: 'clear', timestamp: Date.now()}
    this.#record(entry)
    this.emit('clear', entry)
}

spacer () {
    const entry = {event: 'spacer', timestamp: Date.now()}
    this.#record(entry)
    this.emit('spacer', entry)
}

title (title) {
    const entry = {event: 'title', title, timestamp: Date.now()}
    this.#record(entry)
    this.emit('title', entry)
}

clearHistory () {
