}

methods.source:
getListenersFor (name) {
    return this.#listenersFor[name]
}

on (name, listener) {
    if (typeof listener !== 'function') {
        throw new TypeError('Listener must be a function')
    }

    if (!this.#listenersFor[name]) {
        this.#listenersFor[name] = []
    }

    this.#listenersFor[name].push(listener)

    return listener
}

once (name, listener) {
    if (typeof listener !== 'function') {
        throw new TypeError('Listener must be a function')
    }

    const onceWrapper = (...args) => {
        listener(...args)
        this.off(name, onceWrapper)
    }

    return this.on(name, onceWrapper)
}

off (name, listener) {
    const listeners = this.getListenersFor(name)

    if (typeof listener === 'undefined') {
        return this.removeListenersFor(name)
    } else if (Array.isArray(listeners)) {
        const index = listeners.indexOf(listener)

        if (index !== -1) {
            listeners.splice(index, 1)
            return true
        }
    }

    return false
}

emit (name, ...args) {
    const listeners = this.getListenersFor(name)
    if (!listeners) {
        return
    }

    const listenersCopy = [...listeners]

    for (const listener of listenersCopy) {
        listener.call(this, ...args)
    }
}

emitter (name) {
    return (...args) => this.emit(name, ...args)
}

listenTo (target, eventName, callback) {
    target.on(eventName, callback)
    this.#externalListeners.push({target, eventName, callback})
}

listenToOnce (target, eventName, callback) {
    const onceWrapper = (...args) => {
        callback(...args)

        target.off(eventName, onceWrapper)

        const index = this.#externalListeners.findIndex(
            l => l.target === target && l.eventName === eventName && l.callback === onceWrapper
        )
        if (index !== -1) {
            this.#externalListeners.splice(index, 1)
        }
    }

    target.on(eventName, onceWrapper)
    this.#externalListeners.push({target, eventName, callback: onceWrapper})
}

cleanExternalListeners () {
    this.#externalListeners.forEach(({target, eventName, callback}) => {
        target.off(eventName, callback)
    })
    this.#externalListeners = []
}

removeListeners () {
    Object.keys(this.#listenersFor).forEach(name => this.removeListenersFor(name))
}

removeListenersFor (name) {
    const listeners = this.getListenersFor(name)

    if (listeners) {
        listeners.length = 0
        delete this.#listenersFor[name]
        return true
    }

    return false
}

delegateEvents (target, events, namespace) {
    if (!target || (!Array.isArray(events) && typeof events !== 'object')) {
        return
    }

    const eventArray = Array.isArray(events) ? events : Object.keys(events)
    eventArray.forEach((event) => {
        this.listenTo(target, event, (...args) => {
            const prefixedEvent = namespace ? `${namespace}:${event}` : event
            this.emit(prefixedEvent, ...args)
        })
    })
}

get (key) {
    return this.#map.get(key)
}

has (key) {
    return this.#map.has(key)
}

hasValue (value) {
    return this.#values.has(value)
}

keyFor (value) {
    return this.#keyByValue.get(value)
}

hasEntry (key, value) {
    return this.get(key) === value
}

isKeyOf (key, value) {
    return this.keyFor(value) === key
}

forEach (callbackFn, thisArg) {
    this.#map.forEach(callbackFn, thisArg)
}

set (key, value) {
    const exists = this.#map.has(key)
    const oldValue = exists ? this.#map.get(key) : undefined
    const isReplacing = exists && oldValue !== value

    this.#map.set(key, value)
    this.#values.add(value)
    this.#keyByValue.set(value, key)

    if (isReplacing) {
        this.#removeFromValues(oldValue)
        this.emit('delete', key, oldValue)
    }

    this.emit('set', key, value, oldValue)

    return true
}

delete (key) {
    const exists = this.#map.has(key)

    if (!exists) {
        return false
    }

    const value = this.#map.get(key)
    const deleted = this.#map.delete(key)

    if (deleted) {
        this.#removeFromValues(value)
        this.emit('delete', key, value)
    }

    return deleted
}

updateKey (oldKey, newKey, item) {
    if (!this.has(oldKey)) {
        return false
    }

    if (oldKey === newKey) {
        return false
    }

    const value = this.get(oldKey)

    if (item !== undefined && value !== item) {
        return false
    }

    this.#map.delete(oldKey)
    this.#map.set(newKey, value)

    this.emit('key:updated', oldKey, newKey, value)

    return true
}

clear () {
    if (this.#map.size > 0) {
        const itemsToDelete = Array.from(this.#map.entries())
        itemsToDelete.forEach(([key, value]) => {
            this.emit('delete', key, value)
        })

        this.#map.clear()
        this.#values.clear()
        this.#keyByValue.clear()

        this.emit('clear')
    }
}

addCollection (collection) {
    if (!collection || typeof collection !== 'object') {
        return false
    }

    if (typeof collection.forEach === 'function') {
        collection.forEach((value, key) => {
            this.set(key, value)
        })
    } else {
        for (const key in collection) {
            if (Object.prototype.hasOwnProperty.call(collection, key)) {
                this.set(key, collection[key])
            }
        }
    }

    return true
}

toObject () {
    const object = {}
    this.#map.forEach((value, key) => {
        object[key] = value
    })
    return object
}

