static defaultElement (params) {
    const element = document.createElement('div')

    element.id = params.id || uniqueId('perky_view', 'perky_view')

    element.className = params.className || 'perky-view'

    const styles = {
        display: 'block',
        overflow: 'hidden',
        position: 'relative',
        width: '100%',
        height: '100%',
        ...this.defaultStyles(params)
    }

    Object.assign(element.style, styles)

    return element
}

methods.source:
addClass (className) {
    this.element.classList.add(className)
}

removeClass (className) {
    this.element.classList.remove(className)
}

hasClass (className) {
    return this.element.classList.contains(className)
}

setSize ({width, height, unit = 'px'}) {
    Object.assign(this.element.style, {
        width: `${width}${unit}`,
        height: `${height}${unit}`
    })

    this.emit('resize', {width, height})
}

fit (element = this.container) {
    const {width, height} = element.getBoundingClientRect()

    this.setSize({width, height})
}

mount (container) {
    if (!container) {
        throw new Error('Container element is required')
    }

    if (this.container && this.container !== container) {
        this.container.removeChild(this.element)
    }

    container.appendChild(this.element)

    this.emit('mount', {container})

    if (this.#resizeObserver) {
        this.#resizeObserver.observe(this.element)
    }
}

dismount () {
    if (this.container) {
        this.container.removeChild(this.element)
        this.emit('dismount', {container: this.container})
    }

    if (this.#resizeObserver) {
        this.#resizeObserver.disconnect()
    }
}

isVisible () {
    return this.element.style.display !== 'none'
}

onDispose () {
    this.exitFullscreenMode()

    if (this.#resizeObserver) {
        this.#resizeObserver.disconnect()
        this.#resizeObserver = null
    }

    if (this.#onFullscreenChange) {
        document.removeEventListener('fullscreenchange', this.#onFullscreenChange)
    }

    this.dismount()
}

hide () {
    if (this.display && this.display !== 'none') {
        this.#previousDisplay = this.display
    }

    this.display = 'none'
}

show () {
    if (this.#previousDisplay) {
        this.display = this.#previousDisplay
        this.#previousDisplay = null
    } else {
        this.display = ''
    }
}

setDisplayMode (mode) {
    const modes = {
        normal: () => this.exitFullscreenMode(),
        fullscreen: () => this.enterFullscreenMode()
    }

    if (modes[mode]) {
        modes[mode]()
    }
}

enterFullscreenMode () {
    if (this.displayMode === 'fullscreen') {
        return
    }

    this.displayMode = 'fullscreen'

    document.body.classList.add('fullscreen-mode')

    this.#previousStyles = {
        position: this.element.style.position,
        top: this.element.style.top,
        left: this.element.style.left,
        width: this.element.style.width,
        height: this.element.style.height,
        zIndex: this.element.style.zIndex
    }

    Object.assign(this.element.style, {
        position: 'fixed',
        top: '0',
        left: '0',
        width: '100vw',
        height: '100vh',
        zIndex: '10000'
    })

    this.#requestFullscreen()
}

exitFullscreenMode () {
    if (this.displayMode === 'normal') {
        return
    }

    if (document.fullscreenElement) {
        document.exitFullscreen()
    }

    this.displayMode = 'normal'

    document.body.style.overflow = ''
    document.body.classList.remove('fullscreen-mode')

    Object.assign(this.element.style, this.#previousStyles)
    this.#previousStyles = {}

    this.#dispatchDisplayModeChanged('normal')
}

toggleFullscreen () {
    const mode = this.displayMode

    if (mode === 'fullscreen') {
        this.exitFullscreenMode()
    } else {
        this.enterFullscreenMode()
    }
}

