setValue (value, event = null) {
    if (value === this[VALUE]) {
        return false
    }

    this[OLD_VALUE] = this[VALUE]
    this[VALUE] = value

    this.emit('updated', this[VALUE], this[OLD_VALUE], event)

    return true
}

