constructor (params = {}) {
    super(params)

    this.element = params.element ? params.element : this.constructor.defaultElement(params)

    if (params.position) {
        applyPositionStyle(this.element, params.position)
    }

    if (params.container) {
        this.mount(params.container)
    }

    if (params.className) {
        this.addClass(params.className)
    }


    this.#setupResizeObserver()
    this.#setupFullscreenEvents()
}

methods.source:
getListenersFor (name) {
    return this.#listenersFor[name]
}

on (name, listener) {
    if (typeof listener !== 'function') {
        throw new TypeError('Listener must be a function')
    }

    if (!this.#listenersFor[name]) {
        this.#listenersFor[name] = []
    }

    this.#listenersFor[name].push(listener)

    return listener
}

once (name, listener) {
    if (typeof listener !== 'function') {
        throw new TypeError('Listener must be a function')
    }

    const onceWrapper = (...args) => {
        listener(...args)
        this.off(name, onceWrapper)
    }

    return this.on(name, onceWrapper)
}

off (name, listener) {
    const listeners = this.getListenersFor(name)

    if (typeof listener === 'undefined') {
        return this.removeListenersFor(name)
    } else if (Array.isArray(listeners)) {
        const index = listeners.indexOf(listener)

        if (index !== -1) {
            listeners.splice(index, 1)
            return true
        }
    }

    return false
}

emit (name, ...args) {
    const listeners = this.getListenersFor(name)
    if (!listeners) {
        return
    }

    const listenersCopy = [...listeners]

    for (const listener of listenersCopy) {
        listener.call(this, ...args)
    }
}

emitter (name) {
    return (...args) => this.emit(name, ...args)
}

listenTo (target, eventName, callback) {
    target.on(eventName, callback)
    this.#externalListeners.push({target, eventName, callback})
}

listenToOnce (target, eventName, callback) {
    const onceWrapper = (...args) => {
        callback(...args)

        target.off(eventName, onceWrapper)

        const index = this.#externalListeners.findIndex(
            l => l.target === target && l.eventName === eventName && l.callback === onceWrapper
        )
        if (index !== -1) {
            this.#externalListeners.splice(index, 1)
        }
    }

    target.on(eventName, onceWrapper)
    this.#externalListeners.push({target, eventName, callback: onceWrapper})
}

cleanExternalListeners () {
    this.#externalListeners.forEach(({target, eventName, callback}) => {
        target.off(eventName, callback)
    })
    this.#externalListeners = []
}

removeListeners () {
    Object.keys(this.#listenersFor).forEach(name => this.removeListenersFor(name))
}

removeListenersFor (name) {
    const listeners = this.getListenersFor(name)

    if (listeners) {
        listeners.length = 0
        delete this.#listenersFor[name]
        return true
    }

    return false
}

delegateEvents (target, events, namespace) {
    if (!target || (!Array.isArray(events) && typeof events !== 'object')) {
        return
    }

    const eventArray = Array.isArray(events) ? events : Object.keys(events)
    eventArray.forEach((event) => {
        this.listenTo(target, event, (...args) => {
            const prefixedEvent = namespace ? `${namespace}:${event}` : event
            this.emit(prefixedEvent, ...args)
        })
    })
}

start () {
    if (this.#started) {
        return false
    }

    this.#started = true
    this.onStart?.()
    this.emit('start')

    return true
}

stop () {
    if (!this.#started) {
        return false
    }

    this.#started = false
    this.onStop?.()
    this.emit('stop')

    return true
}

dispose () {
    if (this.#disposed) {
        return false
    }

    this.#disposed = true
    this.stop()

    this.cleanExternalListeners()

    this.#childrenRegistry.forEach(child => {
        if (child && !child.disposed) {
            child.dispose()
        }
    })
    this.#childrenRegistry.clear()

    this.onDispose?.()
    this.emit('dispose')
    this.removeListeners()

    return true
}

install (host, options) {
    if (this.#installed) {
        return this.uninstall()
    }

    this.#host = host
    if (this.$bind) {
        host[this.$bind] = this
    }
    this.#installed = true

    this.onInstall?.(host, options)

    return true
}

uninstall () {
    if (!this.#installed) {
        return false
    }

    this.cleanDelegations()
    this.cleanEventDelegations()
    this.onUninstall?.(this.#host)
    this.#installed = false
    this.#host = null

    return true
}

create (Child, options = {}) {
    options.$category ||= Child.$category
    options.$name ||= Child.$name || options.$category
    options.$id ||= uniqueId(this.#childrenRegistry, options.$name)
    options.$lifecycle = options.$lifecycle ?? Child.$lifecycle ?? true
    options.$eagerStart = options.$eagerStart ?? Child.$eagerStart ?? true

    return this.#addChild(new Child(options), options)
}

getChild (name) {
    return this.#childrenRegistry.get(name) || null
}

hasChild (name) {
    return this.#childrenRegistry.has(name)
}

removeChild (name) {
    const child = this.#childrenRegistry.get(name)

    if (!child) {
        return false
    }

    unregisterChild(this, child)

    return true
}

listNamesFor (key, indexName = '$category') {
    return this.#childrenRegistry.lookupKeys(indexName, key)
}

lookup (indexName, key) {
    return this.#childrenRegistry.lookup(indexName, key)
}

childrenByCategory (category) {
    return this.lookup('$category', category)
}

hasTag (tag) {
    return this.#tags.has(tag)
}

addTag (tag) {
    if (this.#tags.has(tag)) {
        return false
    }
    this.#tags.add(tag)
    return true
}

removeTag (tag) {
    return this.#tags.delete(tag)
}

hasTags (tags) {
    if (typeof tags === 'string') {
        return this.hasTag(tags)
    }

    return Array.isArray(tags) && tags.every(tag => this.#tags.has(tag))
}

childrenByTags (tags) {
    const tagArray = Array.isArray(tags) ? tags : [tags]

    if (tagArray.length === 0) {
        return []
    }

    const indexKey = getTagIndexKey(tagArray)

    if (this.#tagIndexes.has(indexKey)) {
        return this.#childrenRegistry.lookup(indexKey, indexKey)
    }

    return this.#childrenRegistry.all.filter(
        child => tagArray.every(tag => child.$tags?.includes(tag))
    )
}

addTagsIndex (tags) {
    if (!Array.isArray(tags) || tags.length === 0) {
        return false
    }

    const indexKey = getTagIndexKey(tags)

    if (this.#tagIndexes.has(indexKey)) {
        return false
    }

    this.#childrenRegistry.addIndex(indexKey, child => {
        const hasAllTags = tags.every(tag => child.tags?.has(tag))
        return hasAllTags ? indexKey : null
    })

    this.#tagIndexes.set(indexKey, tags)

    this.#childrenRegistry.forEach(child => {
        if (child.tags) {
            this.#setupTagIndexListeners(child)
        }
    })

    return true
}

removeTagsIndex (tags) {
    const indexKey = getTagIndexKey(tags)

    if (!this.#tagIndexes.has(indexKey)) {
        return false
    }

    this.#childrenRegistry.removeIndex(indexKey)
    this.#tagIndexes.delete(indexKey)
    return true
}

delegateTo (host, names) {
    delegateProperties(host, this, names)

    const propertyNames = Array.isArray(names)
        ? names
        : Object.values(names)

    this.#delegations.push({host, propertyNames})
}

cleanDelegations () {
    for (const {host, propertyNames} of this.#delegations) {
        for (const name of propertyNames) {
            delete host[name]
        }
    }
    this.#delegations.length = 0
}

delegateEventsTo (host, events, namespace) {
    const eventArray = Array.isArray(events) ? events : Object.keys(events)
    const callbacks = []

    for (const event of eventArray) {
        const prefixedEvent = namespace ? `${namespace}:${event}` : event
        const callback = (...args) => host.emit(prefixedEvent, ...args)
        this.on(event, callback)
        callbacks.push({event, callback})
    }

    this.#eventDelegations.push({callbacks})
}

cleanEventDelegations () {
    for (const {callbacks} of this.#eventDelegations) {
        for (const {event, callback} of callbacks) {
            this.off(event, callback)
        }
    }
    this.#eventDelegations.length = 0
}

query (selector) {
    return perkyQuery(this, selector)
}

queryAll (selector) {
    return perkyQueryAll(this, selector)
}

