function toCamelCase (string) {
    return inflector.toCamelCase(string)
}

function toPascalCase (string) {
    return inflector.toPascalCase(string)
}

function toSnakeCase (string) {
    return inflector.toSnakeCase(string)
}

function toKebabCase (string) {
    return inflector.toKebabCase(string)
}

function uniqueId (collection, prefix) {
    if (!prefix) {
        prefix = collection
        collection = 'default'
    }

    if (!idCounter[collection]) {
        idCounter[collection] = {}
    }

    if (!idCounter[collection][prefix]) {
        idCounter[collection][prefix] = 0
    }

    const current = idCounter[collection][prefix]
    idCounter[collection][prefix]++

    return current === 0 ? prefix : `${prefix}_${current}`
}

function resetUniqueId (collection, prefix) {
    if (prefix) {
        if (idCounter[collection]) {
            idCounter[collection][prefix] = 0
        }
    } else {
        idCounter[collection] = {}
    }
}

function singularize (word) {
    return inflector.singular(word)
}

function pluralize (word, count, inclusive) {
    return inflector.pluralize(word, count, inclusive)
}

function plural (word) {
    return inflector.plural(word)
}

function isPlural (word) {
    return inflector.isPlural(word)
}

function isSingular (word) {
    return inflector.isSingular(word)
}

function setDefaults (data, defaults) {
    return deepMerge(defaults, data)
}

function getNestedValue (obj, path) {
    if (!path) {
        return obj
    }

    const parts = path.split('.')
    let current = obj

    for (const part of parts) {
        if (current === undefined || current === null) {
            return undefined
        }
        current = current[part]
    }

    return current
}

function setNestedValue (obj, path, value) {
    const parts = path.split('.')
    let current = obj

    for (let i = 0; i < parts.length - 1; i++) {
        const part = parts[i]
        if (!current[part]) {
            current[part] = {}
        }
        current = current[part]
    }

    current[parts[parts.length - 1]] = value
    return obj
}

function deepMerge (target, source) {
    return deepMergeInternal(target, source, new WeakSet())
}

function deepMergeInternal (target, source, seen) {
    if (!source) {
        return target
    }

    if (typeof source === 'object' && source !== null) {
        if (seen.has(source)) {
            return source
        }
        seen.add(source)
    }

    const sourceIsArray = Array.isArray(source)
    const targetIsArray = Array.isArray(target)

    if (sourceIsArray !== targetIsArray) {
        return cloneIfNeeded(source, seen)
    }

    if (sourceIsArray) {
        return mergeArrays(target, source, seen)
    }

    return mergeObject(target, source, seen)
}

function exportValue (value) {

    if (value && typeof value === 'object') {
        if (typeof value.export === 'function') {
            return value.export()
        }

        if (Array.isArray(value)) {
            return value.map(item => exportValue(item))
        }

        const result = {}
        for (const key in value) {
            if (Object.hasOwn(value, key)) {
                result[key] = exportValue(value[key])
            }
        }
        return result
    }

    return value
}

function isNonNullObject (value) {
    return Boolean(value) && typeof value === 'object'
}

function isSpecial (value) {
    const stringValue = Object.prototype.toString.call(value)
    return stringValue === '[object RegExp]' || stringValue === '[object Date]'
}

function isMergeableObject (value) {
    return isNonNullObject(value) && !isSpecial(value)
}

function emptyTarget (value) {
    return Array.isArray(value) ? [] : {}
}

function cloneIfNeeded (value, seen) {
    return isMergeableObject(value) ? deepMergeInternal(emptyTarget(value), value, seen) : value
}

function mergeArrays (target, source, seen) {
    return target.concat(source).map(element => cloneIfNeeded(element, seen))
}

function getKeys (target) {
    const symbols = Object.getOwnPropertySymbols
        ? Object.getOwnPropertySymbols(target).filter(symbol =>
            Object.propertyIsEnumerable.call(target, symbol))
        : []
    return Object.keys(target).concat(symbols)
}

function mergeObject (target, source, seen) {
    const destination = {}

    if (isMergeableObject(target)) {
        getKeys(target).forEach(key => {
            destination[key] = cloneIfNeeded(target[key], seen)
        })
    }

    getKeys(source).forEach(key => {
        if ((key in target) && isMergeableObject(source[key])) {
            destination[key] = deepMergeInternal(target[key], source[key], seen)
        } else {
            destination[key] = cloneIfNeeded(source[key], seen)
        }
    })

    return destination
}

function formatNumber (n) {
    if (typeof n !== 'number') {
        return String(n)
    }
    return Number.isInteger(n) ? String(n) : n.toFixed(2)
}

function formatBytes (bytes) {
    if (bytes === 0) {
        return '0 B'
    }

    const units = ['B', 'KB', 'MB', 'GB']
    const i = Math.floor(Math.log(bytes) / Math.log(1024))
    const value = bytes / Math.pow(1024, i)

    return `${value.toFixed(i > 1 ? 2 : 0)} ${units[i]}`
}

function delegateProperties (receiver, source, names) {
    if (Array.isArray(names)) {
        names.forEach(name => delegateProperty(receiver, source, name, name))
    } else if (typeof names === 'object') {
        Object.entries(names).forEach(([sourceName, receiverName]) => {
            delegateProperty(receiver, source, sourceName, receiverName)
        })
    }
}

function delegateProperty (receiver, source, sourceName, receiverName) {
    const descriptor = Object.getOwnPropertyDescriptor(source, sourceName)

    if (descriptor && (descriptor.get || descriptor.set)) {
        Object.defineProperty(receiver, receiverName, {
            get: descriptor.get ? descriptor.get.bind(source) : undefined,
            set: descriptor.set ? descriptor.set.bind(source) : undefined,
            enumerable: true,
            configurable: true
        })
    } else if (typeof source[sourceName] === 'function') {
        receiver[receiverName] = source[sourceName].bind(source)
    } else {
        Object.defineProperty(receiver, receiverName, {
            get: () => source[sourceName],
            set: (value) => {
                source[sourceName] = value
            },
            enumerable: true,
            configurable: true
        })
    }
}

methods.source:
connectedCallback () {
    this.#buildDOM()
    this.#bindLoggerEvents()
}

disconnectedCallback () {
    this.#unbindLoggerEvents()
}

attributeChangedCallback (name, oldValue, newValue) {
    if (oldValue === newValue) {
        return
    }

    this.#handleAttributeChange(name, newValue)
}

log (message, type = 'info', format = 'text', timestamp = null) {
    const entry = document.createElement('div')
    entry.className = `logger-entry log-${type}`

    const indicator = document.createElement('span')
    indicator.className = 'logger-indicator'
    entry.appendChild(indicator)

    const messageElement = document.createElement('span')
    messageElement.className = 'logger-message'

    processMessage(messageElement, message, format)

    entry.appendChild(messageElement)

    const time = timestamp ? new Date(timestamp) : new Date()
    const timestampEl = document.createElement('span')
    timestampEl.className = 'logger-timestamp'
    timestampEl.textContent = time.toLocaleTimeString()
    entry.appendChild(timestampEl)

    this.#entries.push(entry)

    while (this.#entries.length > this.#maxEntries) {
        const removed = this.#entries.shift()
        if (removed.parentNode) {
            removed.parentNode.removeChild(removed)
        }
    }

    if (this.#contentEl) {
        this.#contentEl.appendChild(entry)
        this.#contentEl.scrollTop = this.#contentEl.scrollHeight
    }

    this.#updateControlsVisibility()

    return entry
}

info (...messages) {
    return this.log(formatMessage(...messages), 'info')
}

notice (...messages) {
    return this.log(formatMessage(...messages), 'notice')
}

warn (...messages) {
    return this.log(formatMessage(...messages), 'warn')
}

error (...messages) {
    return this.log(formatMessage(...messages), 'error')
}

success (...messages) {
    return this.log(formatMessage(...messages), 'success')
}

spacer () {
    const entry = document.createElement('div')
    entry.className = 'logger-entry logger-spacer'
    this.#entries.push(entry)

    if (this.#contentEl) {
        this.#contentEl.appendChild(entry)
    }
}

title (title) {
    const entry = document.createElement('div')
    entry.className = 'logger-entry logger-title-entry'
    entry.textContent = title
    this.#entries.push(entry)

    if (this.#contentEl) {
        this.#contentEl.appendChild(entry)
    }
}

clear () {
    this.#entries = []
    if (this.#contentEl) {
        this.#contentEl.innerHTML = ''
    }
    this.#updateControlsVisibility()
}

setters.source:
set maxEntries (value) {
    this.#maxEntries = value
    this.setAttribute('max-entries', value)
}

set position (value) {
    this.#position = value
    this.setAttribute('position', value)
    this.#updateClasses()
}

set timestamp (value) {
    this.#timestamp = value
    if (value) {
        this.setAttribute('timestamp', '')
    } else {
        this.removeAttribute('timestamp')
    }
}

set theme (value) {
    this.#theme = value
    if (value) {
        this.setAttribute('theme', value)
    } else {
        this.removeAttribute('theme')
    }
}

methods.source:
pause (...args) {
    if (!this.started || this.#paused) {
        return false
    }

    this.#paused = true
    this.emit('pause', ...args)

    return true
}

resume (...args) {
    if (!this.started || !this.#paused) {
        return false
    }

    this.#paused = false
    this.lastTime = performance.now()
    this.emit('resume', ...args)

    requestAnimationFrame(time => update(this, time))

    return true
}

getFps () {
    return this.fps
}

setFps (fps) {
    this.fps = fps
    this.frameInterval = 1000 / fps
    this.emit('changed:fps', fps)
}

getCurrentFps () {
    return this.currentFps || 0
}

getScreenFps () {
    return this.screenFps || 0
}

setFpsLimited (value) {
    this.#fpsLimited = value
    this.emit('changed:fpsLimited', value)
}

onStart () {
    this.container.addEventListener('keydown', this.#keydownListener, true)
    this.container.addEventListener('keyup', this.#keyupListener, true)
    this.container.addEventListener('blur', this.#blurListener)
}

onStop () {
    this.container.removeEventListener('keydown', this.#keydownListener, true)
    this.container.removeEventListener('keyup', this.#keyupListener, true)
    this.container.removeEventListener('blur', this.#blurListener)
}

