static generateSeed (length = 10) {
    let result = ''

    for (let i = 0; i < length; i++) {
        result += CHARACTERS.charAt(Math.floor(Math.random() * CHARACTERS.length))
    }

    return result
}

methods.source:
setSeed (seed) {
    this.#seed = seed
    this.#initSeed(seed)

    return this
}

getSeed () {
    return this.#seed
}

getState () {
    return {
        c: this.#c,
        s0: this.#s0,
        s1: this.#s1,
        s2: this.#s2
    }
}

fork () {
    const forked = new Random(this.#seed)
    forked.setState(this.getState())

    return forked
}

between (min, max) {
    return this.#next() * (max - min) + min
}

intBetween (min, max) {
    return Math.floor(this.between(min, max))
}

pick (array) {
    return array[this.intBetween(0, array.length)]
}

oneChanceIn (chances) {
    return this.#next() < 1 / chances
}

coinToss () {
    return this.#next() < 0.5
}

weightedChoice (choices) {
    if (!choices || choices.length === 0) {
        return undefined
    }

    const totalWeight = choices.reduce((total, choice) => total + choice.weight, 0)

    let randomValue = this.#next() * totalWeight

    for (const choice of choices) {
        randomValue -= choice.weight

        if (randomValue <= 0) {
            return choice.value
        }
    }

    return choices[choices.length - 1].value
}

set (x, y) {
    this.x = x
    this.y = y
    return this
}

setX (x) {
    this.x = x
    return this
}

setY (y) {
    this.y = y
    return this
}

setScalar (scalar) {
    this.x = scalar
    this.y = scalar
    return this
}

copy (v) {
    this.x = v.x
    this.y = v.y
    return this
}

clone () {
    return new Vec2(this.x, this.y)
}

add (v) {
    this.x += v.x
    this.y += v.y
    return this
}

addScalar (s) {
    this.x += s
    this.y += s
    return this
}

addVectors (a, b) {
    this.x = a.x + b.x
    this.y = a.y + b.y
    return this
}

addScaledVector (v, s) {
    this.x += v.x * s
    this.y += v.y * s
    return this
}

sub (v) {
    this.x -= v.x
    this.y -= v.y
    return this
}

subScalar (s) {
    this.x -= s
    this.y -= s
    return this
}

subVectors (a, b) {
    this.x = a.x - b.x
    this.y = a.y - b.y
    return this
}

multiply (v) {
    this.x *= v.x
    this.y *= v.y
    return this
}

multiplyScalar (scalar) {
    this.x *= scalar
    this.y *= scalar
    return this
}

divide (v) {
    this.x /= v.x
    this.y /= v.y
    return this
}

divideScalar (scalar) {
    return this.multiplyScalar(1 / scalar)
}

dot (v) {
    return this.x * v.x + this.y * v.y
}

cross (v) {
    return this.x * v.y - this.y * v.x
}

lengthSq () {
    return this.x * this.x + this.y * this.y
}

length () {
    return Math.sqrt(this.lengthSq())
}

manhattanLength () {
    return Math.abs(this.x) + Math.abs(this.y)
}

normalize () {
    return this.divideScalar(this.length() || 1)
}

angle () {
    return Math.atan2(-this.y, -this.x) + Math.PI
}

angleTo (v) {
    const denominator = Math.sqrt(this.lengthSq() * v.lengthSq())
    if (denominator === 0) {
        return Math.PI / 2
    }
    const theta = this.dot(v) / denominator
    return Math.acos(Math.max(-1, Math.min(1, theta)))
}

distanceTo (v) {
    return Math.sqrt(this.distanceToSquared(v))
}

distanceToSquared (v) {
    const dx = this.x - v.x
    const dy = this.y - v.y
    return dx * dx + dy * dy
}

manhattanDistanceTo (v) {
    return Math.abs(this.x - v.x) + Math.abs(this.y - v.y)
}

setLength (length) {
    return this.normalize().multiplyScalar(length)
}

lerp (v, alpha) {
    this.x += (v.x - this.x) * alpha
    this.y += (v.y - this.y) * alpha
    return this
}

lerpVectors (v1, v2, alpha) {
    this.x = v1.x + (v2.x - v1.x) * alpha
    this.y = v1.y + (v2.y - v1.y) * alpha
    return this
}

equals (v) {
    return v.x === this.x && v.y === this.y
}

fromArray (array, offset = 0) {
    this.x = array[offset]
    this.y = array[offset + 1]
    return this
}

toArray (array = [], offset = 0) {
    array[offset] = this.x
    array[offset + 1] = this.y
    return array
}

negate () {
    this.x = -this.x
    this.y = -this.y
    return this
}

min (v) {
    this.x = Math.min(this.x, v.x)
    this.y = Math.min(this.y, v.y)
    return this
}

max (v) {
    this.x = Math.max(this.x, v.x)
    this.y = Math.max(this.y, v.y)
    return this
}

clamp (min, max) {
    this.x = Math.max(min.x, Math.min(max.x, this.x))
    this.y = Math.max(min.y, Math.min(max.y, this.y))
    return this
}

clampScalar (minVal, maxVal) {
    this.x = Math.max(minVal, Math.min(maxVal, this.x))
    this.y = Math.max(minVal, Math.min(maxVal, this.y))
    return this
}

clampLength (min, max) {
    const length = this.length()
    return this.divideScalar(length || 1).multiplyScalar(
        Math.max(min, Math.min(max, length))
    )
}

floor () {
    this.x = Math.floor(this.x)
    this.y = Math.floor(this.y)
    return this
}

ceil () {
    this.x = Math.ceil(this.x)
    this.y = Math.ceil(this.y)
    return this
}

round () {
    this.x = Math.round(this.x)
    this.y = Math.round(this.y)
    return this
}

roundToZero () {
    this.x = Math.trunc(this.x)
    this.y = Math.trunc(this.y)
    return this
}

rotateAround (center, angle) {
    const c = Math.cos(angle)
    const s = Math.sin(angle)

    const x = this.x - center.x
    const y = this.y - center.y

    this.x = x * c - y * s + center.x
    this.y = x * s + y * c + center.y

    return this
}

random () {
    this.x = Math.random()
    this.y = Math.random()
    return this
}

getComponent (index) {
    if (index === 0) {
        return this.x
    } else if (index === 1) {
        return this.y
    }
    throw new Error(`index out of range: ${index}`)
}

setComponent (index, value) {
    if (index === 0) {
        this.x = value
    } else if (index === 1) {
        this.y = value
    } else {
        throw new Error(`index out of range: ${index}`)
    }
    return this
}

