constructor (x = 0, y = 0) {
    if (typeof x === 'object') {
        if (Array.isArray(x)) {
            this.x = x[0] ?? 0
            this.y = x[1] ?? 0
        } else {
            this.x = x.x ?? 0
            this.y = x.y ?? 0
        }
    } else {
        this.x = x
        this.y = y
    }
}

