const palette = document.createElement('devtools-command-palette')
document.body.appendChild(palette)

palette.setState({
    appManager: {list: () => []},
    closeCommandPalette: () => palette.hide()
})

palette.show()

const palette = document.createElement('devtools-command-palette')

const mockState = {
    appManager: {
        list: () => [{
            $id: 'game',
            $status: 'started',
            actionDispatcher: {
                listAllActions: () => new Map([
                    ['player', [
                        {name: 'jump', params: []},
                        {name: 'shoot', params: []},
                        {name: 'move', params: [{name: 'x'}, {name: 'y'}]}
                    ]],
                    ['ui', [
                        {name: 'openMenu', params: []},
                        {name: 'closeMenu', params: []}
                    ]]
                ]),
                execute: (name, ...args) => {
                    logger.log(`Executing: ${name}`, ...args)
                }
            }
        }]
    },
    closeCommandPalette: () => palette.hide(),
    openTool: (id) => logger.log(`Opening tool: ${id}`),
    openLogger: () => logger.log('Opening logger'),
    toggleLogger: () => logger.log('Toggling logger'),
    closeSidebar: () => logger.log('Closing sidebar')
}

palette.setState(mockState)
ctx.container.appendChild(palette)

palette.show()
