class MyButton extends PerkyComponent {
    static styles = `
        button {
            background: var(--accent);
            color: var(--bg-primary);
            border: none;
            padding: 8px 16px;
            border-radius: 4px;
        }
    `

    onConnected () {
        this.shadowRoot.innerHTML = '<button>Click me</button>'
    }
}

class BasePanel extends EditorComponent {
    static styles = `
        :host { display: block; padding: 12px; }
    `
}

class FancyPanel extends BasePanel {
    static styles = `
        :host { border: 1px solid var(--border); }
    `
}

class Counter extends EditorComponent {
    count = 0

    onConnected () {
        this.render()
    }

    render () {
        this.shadowRoot.innerHTML = `<span>${this.count}</span>`
    }
}

class PlayerHealth extends EditorComponent {
    setPlayer (player) {
        this.listenTo(player, 'damage', () => this.render())
        this.listenTo(player, 'heal', () => this.render())
    }
}

class MyInspector extends EditorComponent {
    static styles = `
        .label { color: var(--fg-muted); }
        .value { color: var(--fg-primary); }
    `
}

class MyPanel extends EditorComponent {
    static styles = myPanelStyles
}
