const binding = new InputBinding({
    deviceName: 'keyboard',
    controlName: 'KeyW',
    actionName: 'moveUp'
})

logger.log(binding.key)

const withController = new InputBinding({
    deviceName: 'keyboard',
    controlName: 'KeyW',
    actionName: 'moveUp',
    controllerName: 'player1'
})

logger.log(withController.key)

const binding = new InputBinding({
    deviceName: 'keyboard',
    controlName: 'Space',
    actionName: 'jump'
})

logger.log('Matches Space pressed:', binding.matches({
    deviceName: 'keyboard',
    controlName: 'Space',
    eventType: 'pressed'
}))

logger.log('Matches Space released:', binding.matches({
    deviceName: 'keyboard',
    controlName: 'Space',
    eventType: 'released'
}))

const binding = new InputBinding({
    deviceName: 'keyboard',
    controlName: 'Space',
    actionName: 'jump'
})

logger.log('Before:', binding.deviceName, binding.controlName)

binding.updateInput({
    deviceName: 'keyboard',
    controlName: 'KeyW'
})

logger.log('After:', binding.deviceName, binding.controlName)
