const psd = parsePsd(new Uint8Array(buffer))

psd.width         // 512
psd.height        // 512
psd.colorMode     // 'RGB'
psd.colorProfile  // {name: 'sRGB', isP3: false}
psd.layers        // flat array of layer records
psd.tree          // [{type: 'group', name: 'anim - idle', children: [...]}]
psd.animations    // {idle: [layer, layer, ...], run: [...]}
