const renderer = new WebGLCanvas2D({
    container: ctx.container,
    autoFit: true,
    backgroundColor: '#16213e'
})

const scene = new Group2D()

scene.add(new Rectangle({
    width: 3,
    height: 3,
    color: '#e94560',
    x: -5,
    y: 0
}))

scene.add(new Circle({
    radius: 1.5,
    color: '#0f3460',
    x: 0,
    y: 0
}))

scene.add(new Rectangle({
    width: 2,
    height: 4,
    color: '#533483',
    x: 5,
    y: 0
}))

renderer.render(scene)
ctx.setApp(renderer)

const renderer = new WebGLCanvas2D({
    container: ctx.container,
    autoFit: true,
    backgroundColor: '#1a1a2e'
})

const scene = new Group2D()

const group = new Group2D({x: 0, y: 0, rotation: Math.PI / 8})

group.add(new Rectangle({
    width: 2.5,
    height: 2.5,
    color: '#e94560',
    x: -3,
    y: 0
}))

group.add(new Rectangle({
    width: 2.5,
    height: 2.5,
    color: '#0f3460',
    x: 3,
    y: 0
}))

scene.add(group)
renderer.render(scene)
ctx.setApp(renderer)

logger.log('group rotation:', (group.rotation * 180 / Math.PI).toFixed(0) + 'Â°')

const renderer = new WebGLCanvas2D({
    container: ctx.container,
    autoFit: true,
    backgroundColor: '#16213e'
})

const scene = new Group2D()

for (let i = 0; i < 5; i++) {
    scene.add(new Circle({
        radius: 1,
        color: `hsl(${i * 60}, 70%, 50%)`,
        x: (i - 2) * 3,
        y: 0
    }))
}

renderer.camera.zoom = 1.5
renderer.render(scene)
ctx.setApp(renderer)

logger.log('zoom:', renderer.camera.zoom + 'x')

