class CustomRenderer extends WebGLObjectRenderer {

    static get handles () {
        return [CustomObject]
    }

    flush (matrices) {
        for (const {object, opacity} of this.collected) {
            drawCustomObject(object, opacity)
        }
    }

}

class MyRenderer extends WebGLObjectRenderer {
    static get handles () {
        return [MyObject]
    }
}
